    "url": "http://localhost:4000/csp_apps_pic32mk/apps/spi/master/spi_write_read_test_app/readme.html#downloading-and-building-the-application",
    "relUrl": "/apps/spi/master/spi_write_read_test_app/readme.html#downloading-and-building-the-application"
